"use strict";

var utility = require('../utility/utility');

function KafkaService() {
  this.sendToKafka = function (config, gtmData) {
    var kafkaServiceUrl = config.kafkaServiceUrl;
    utility.xhrRequest('POST', kafkaServiceUrl, {}, [gtmData]).then(function (result) {})["catch"](function (error) {});
  };
}

module.exports = new KafkaService();